function limpa_formul\u00e1rio_cep(){document.getElementById("rua").value="",document.getElementById("bairro").value="",document.getElementById("cidade").value=""}function meu_callback(e){"erro"in e?(limpa_formul\u00e1rio_cep(),alert("CEP n\xe3o encontrado.")):(document.getElementById("rua").value=e.logradouro,document.getElementById("bairro").value=e.bairro,document.getElementById("cidade").value=e.localidade)}function pesquisacep(e){var a=e.replace(/\D/g,"");if(""!=a)if(/^[0-9]{8}$/.test(a)){document.getElementById("rua").value="...",document.getElementById("bairro").value="...",document.getElementById("cidade").value="...";var t=document.createElement("script");t.src="https://viacep.com.br/ws/"+a+"/json/?callback=meu_callback",document.body.appendChild(t)}else limpa_formul\u00e1rio_cep(),alert("Formato de CEP inv\xe1lido.");else limpa_formul\u00e1rio_cep()}